import React from 'react';
import { useEffect, useState } from 'react';
import './HomePage.css';

const HomePage = () => {
  var mode = 'Cut';

  const [showInfo, setShowInfo] = useState(false);

  useEffect(() => {
    const handleKeyPress = (e) => {
      if (showInfo) {
        window.alert(showInfo)
        if (e.key === 'Enter' || e.key === 'Space') {
          document.querySelector('.start-btn').click();
        } else if (e.key === 'm' || e.key === 'M') {
          document.querySelector('.mode-btn').click();
        } else if (e.key === 'a' || e.key === 'A') {
          document.querySelector('.about-btn').click();
        }
      }
    };
    window.addEventListener('keyup', handleKeyPress);
    return () => {
      window.removeEventListener('keyup', handleKeyPress);
    };
  }, []);

  const handleStart = () => {
    setShowInfo(true);
  };

  return (
    <div className="HomeBase">
      <div className="game-container">
        <div className="symbol">
          <img
            src="others/VsK_RPG_Py.png"
            alt="Logo Not Found"
            title="Verma's Karma"
          />
        </div>
        {!showInfo ? (
          <div className="menu">
            <button className="start-btn" onClick={handleStart}>
              Start
            </button>
            <button className="mode-btn">Mode &lt; {mode} &gt;</button>
            <button className="about-btn">About</button>
          </div>
        ) : (
          <div className="infoBox">
            <div className="info">
              -&gt; You are Mr. James Verma, Currently the Temporary CEO of X.
              X's Former CEO has Resigned for Personal Reasons and now its your
              time to shine . . .
              <br />
              <br />
              -&gt; You have to Showcase your Potential at your Upcoming Company
              Summit to your Co-Workers and to the Chief Guest Mr. Phunsuk
              Wangdu, with their Support you can Become the Undisputed CEO and
              Rise to New Heights!
              <br />
              <br />
              <hr />
              NOTE: You can Play with the Following Functionalities:
              <br />
              <br />
              -&gt; Next / Skip Button (&gt;) to Proceed to Next Scene / Fast
              Forward - Keys: 'Enter' / 'Space'
              <br />
              <br />
              -&gt; Get-MyPic Button to Capture Your Face for Emotion Detection
              - Keys: 'c' / 'C'
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default HomePage;
