import './Dialogue.css';
import { useTypewriter, Cursor } from 'react-simple-typewriter';
import { useState, useEffect } from 'react';

var Next = 0;
var Alt = 0;

// function inc_Counter()
// {
//     counter++;
//     console.log(counter);
// }

function Dialogue() {
    // const a = StartGame();
    const [counter,setCounter] = useState(0);
    const [data,setData] = useState([{}])
    useEffect(()=> {
        console.log(counter)
        fetch('http://localhost:5001/dialoge/' + counter)
        .then(resp => resp.json())
        .catch(error =>
        {
            const k = {name : "Error", body : "Error"};
            setData(JSON.parse(k));
        })
        .then(dialoge =>
        {
            setData(dialoge);
            console.log(data);
        })
    console.log(data);

    // const name = data.name;
    // const dialogue = data.body;
    },[counter])
    Next = data.Next;
    Alt = data.Skip;

    const [typeEffect] = useTypewriter({
        words: [data.body == null ? "" : data.body],
        loop: {},
        typeSpeed: 60,
        deleteSpeed: 40
    });

    // Scene Image Manipulation
    const [currentScn, setCurrentScn] = useState(0);
    const images = [
        'sampleScene.png',
        'sampleSceneNext.'
    ];
    const handleNextScn = () => {
        setCurrentScn((prevImageScn) => (prevImageScn + 1) % imagesScn.length);
    };

    return (
      <div className="DialogueBox">
        <img
          className="ScnImg"
          src={require('./sampleScene.png')}
          title="Scene - X"
          alt={"Scene - X" + ' - Image Not Found'}
          key={counter}
          style={{ animation: 'flash 1.5s .5s' }}
        />
        <img
          className="ChrImg"
          src={require('./sampleBob.png')}
          title={data.name}
          alt={data.name + ' - Image Not Found'}
        />
        <div className="DialogueBG">
          <div className="NameTag">{data.name}</div>
          <div className="Dialogues">
            {typeEffect}
          </div>
          <div className="Next">
            <button className="NextButton" onClick={() => setCounter(Next)}>
              Next - {counter}
            </button>
          </div>
        </div>
      </div>
    );
}

export default Dialogue;
