import './Dialogue.css';
import Typewriter from 'typewriter-effect';
import { useState,useEffect } from 'react';

var Next = 0;
var Alt = 0;

// function inc_Counter()
// {
//     counter++;
//     console.log(counter);
// }

function Dialogue() {
    // const a = StartGame();
    const [counter,setCounter] = useState(0);
    const [data,setData] = useState([{}])
    useEffect(()=> {
        console.log(counter)
        fetch('http://localhost:5001/dialoge/' + counter)
        .then(resp => resp.json())
        .catch(error =>
        {
            const k = {name : "error", body : "error"};
            setData(JSON.parse(k));
        })
        .then(dialoge =>
        {
            setData(dialoge);
            console.log(data);
        })
    console.log(data);

    // const name = data.name;
    // const dialogue = data.body;
    },[counter])
    Next = data.Next;
    Alt = data.Skip;
    return (
      <div className="DialogueBox">
        <div className="DialogueBG">
          <div className="NameTag">{data.name}</div>
          <div className="Dialogues">
            <Typewriter
              onInit={(text) => {
                var str = data.body
                text.typeString(str);
              }}
            />
          </div>
          <div className="Next">
            <button className="NextButton" onClick={() => setCounter(Next)}>
              Next - {counter}
            </button>
          </div>
        </div>
      </div>
    );
}

export default Dialogue;
