import React from 'react';
import { useEffect, useState } from 'react';
import './HomePage.css';

const HomePage = () => {
  var mode = 'Cut';

  const [showInfo, setShowInfo] = useState(false);

  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.key === 's' || e.key === 'Enter') {
        document.querySelector('.start-btn').click();
      } else if (e.key === 'm') {
        document.querySelector('.mode-btn').click();
      } else if (e.key === 'a') {
        document.querySelector('.about-btn').click();
      }
    };
    window.addEventListener('keyup', handleKeyPress);
    return () => {
      window.removeEventListener('keyup', handleKeyPress);
    };
  }, []);

  const handleStart = () => {
    setShowInfo(true);
  };

  return (
    <div className="HomeBase">
      <div className="game-container">
        <div className="symbol">
          <img
            src="others/VsK_RPG_Py.png"
            alt="Logo Not Found"
            title="Verma's Karma"
          />
        </div>
        {!showInfo ? (
          <div className="menu">
            <button className="start-btn" onClick={handleStart}>
              Start
            </button>
            <button className="mode-btn">Mode &lt; {mode} &gt;</button>
            <button className="about-btn">About</button>
          </div>
        ) : (
          <div className="infoBox">
            <div className="info">
              You are Mr. James Verma, The new temporary CEO of X, X's former
              CEO has resigned for personal reasons and now its your time to
              shine â€¦ 2: You have to showcase your potential at your upcoming
              company summit to your co-workers and to the Chief Guest
              Mr.Phunsuk Wangdu, with their support you can become the
              undisputed CEO and rise to new heights
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default HomePage;
