import { useCallback, useEffect, useRef, useState } from "react";
import Webcam from "react-webcam";
import './WebCam.css';

const ImgCapture= () =>
{
    const webCamRef = useRef(null);
    const [imgSrc,imgCapt] = useState(null)

    const capture = useCallback(() =>
    {
        imgCapt(webCamRef.current.getScreenshot());
    }, [webCamRef]);

    const resetCamera = useCallback(()=>
    {
        imgCapt(null); 
    });

    useEffect(()=>
    {
        if(imgSrc != null)
        {
            fetch(`http://localhost:5001/imageProcessing`,{
                    'method':'POST',
                    headers : {
                        'Content-Type':'application/json',
                    },
                    body:JSON.stringify({'img':imgSrc})
                })
                .then(response => response.json())
                .then(response => console.log(response))
                .catch(error => console.log(error)) 
        }
    },[imgSrc])

    return (
    <div className="Container">
        {imgSrc ? (
            <img src={imgSrc} alt="webcam" />
        ) : (
            <Webcam className="WebCam" ref={webCamRef} />
            )}
        <button className="GetPic" onClick={imgSrc!=null ? resetCamera:capture}>Get - MyPic</button>
    </div>
    );
};
export default ImgCapture;
