import './SceneHandler.css';
import Typewriter from './ShowDialogue';
// import { Typewriter, useTypewriter, Cursor } from 'react-simple-typewriter';
import { useState, useEffect } from 'react';

var Next = 0;
var Alt = 0;

var ScnSrcOg = 'scenes/sampleScene.png';
var ScnSrc = 'scenes/sampleScene.png';
var ChrSrcOg = 'chars/sampleBob.png';
var ChrSrc = 'chars/sampleBob.png';

function SceneHandler() {
  const [counter, setCounter] = useState(0);
  const [data, setData] = useState([{}]);
  useEffect(() => {
    fetch('http://localhost:5001/dialogue/' + counter)
      .then((resp) => resp.json())
      .catch((error) => {
        const K = {
          name: 'Error',
          body: 'Error',
          scnImg: 'Error',
          chrImg: 'Error',
        };
        setData(JSON.parse(K));
      })
      .then((dialogue) => {
        setData(dialogue);
      });
  }, [counter]);

  Next = data.Next;
  Alt = data.Skip;

  ScnSrcOg = data.scnImg == null ? ScnSrcOg : 'scenes/' + data.scnImg;
  const [ScnSrc,setScnSrc] = useState(ScnSrcOg);

  ChrSrcOg = data.chrImg == null ? ChrSrcOg : 'chars/' + data.chrImg;
  const [ChrSrc, setChrSrc] = useState(ChrSrcOg);

  const [animate_scene, setAnimatorScn] = useState(0);
  const [animate_chars, setAnimatorChr] = useState(0);

  useEffect(() => {
    setAnimatorScn(1);
    setTimeout(() => {
      setScnSrc(ScnSrcOg);
    }, 500);
  }, [ScnSrcOg.toString()]);

  useEffect(() => {
    setAnimatorChr(1);
    setTimeout(() => {
      setChrSrc(ChrSrcOg);
    }, 500);
  }, [ChrSrcOg.toString()]);

  // const [typeEffect] = useTypewriter({
  //   words: [data.body == null ? '' : data.body],
  //   loop: {},
  //   typeSpeed: 45,
  //   deleteSpeed: 33
  // });
  const [showNext, setShowNext] = useState(false);
  const [onStart, setStart] = useState(true);

  const dialogues = [
    'Hello, how are you?',
    "I'm a React component.",
    'Let me show you a typewriter effect.',
  ];
  const [currentDialogueIndex, setCurrentDialogueIndex] = useState(0);

  const handleNext = () => {
    setCurrentDialogueIndex((prevIndex) => (prevIndex + 1) % dialogues.length);
  };

  const handleTypewriterFinish = () => {
    setShowNext(true);
  };
  
  return (
    <div className="DialogueBox">
      <img
        className="ScnImg"
        src={ScnSrc}
        title="Scene - X"
        alt={'Scene - X' + ' - Image Not Found'}
        onAnimationEnd={() => setAnimatorScn(0)}
        animate_scene={animate_scene}
      />
      <img
        className="ChrImg"
        src={ChrSrc}
        title={data.name}
        alt={data.name + ' - Image Not Found'}
        onAnimationEnd={() => setAnimatorChr(0)}
        animate_chars={animate_chars}
      />
      <div className="DialogueBG">
        <div className="NameTag">{data.name}</div>
        <div className="Dialogues">
          <Typewriter
            settingStart = {setStart}
            id = 'dSpace'
            onStart={true}
            text={dialogues[currentDialogueIndex]}
            speed={50}
            onFinish={handleTypewriterFinish}
          />
        </div>
        <div className="Next">
          <button
            className="NextButton"
            onClick={() => {
              setCounter(Next);
              handleNext();
            }}
          >
            Next - {counter}
          </button>
        </div>
      </div>
    </div>
  );
}

export default SceneHandler;
