import './SceneHandler.css';
import { useTypewriter, Cursor } from 'react-simple-typewriter';
import { useState, useEffect } from 'react';

var Next = 0;
var Alt = 0;

var ScnSrc1 = 'scenes/sampleScene.png';
var ScnSrc = 'scenes/sampleScene.png';
var ChrSrc = 'chars/sampleBob.png';

function SceneHandler() {
  const [counter, setCounter] = useState(0);
  const [data, setData] = useState([{}]);
  useEffect(() => {
    console.log(counter);
    fetch('http://localhost:5001/dialogue/' + counter)
      .then((resp) => resp.json())
      .catch((error) => {
        const K = {
          name: 'Error',
          body: 'Error',
          scnImg: 'Error',
          chrImg: 'Error',
        };
        setData(JSON.parse(K));
      })
      .then((dialogue) => {
        setData(dialogue);
        console.log(data);
      });
    console.log(data);
  }, [counter]);

  Next = data.Next;
  Alt = data.Skip;

  ScnSrc1 = data.scnImg == null ? ScnSrc1 : 'scenes/' + data.scnImg;
  const [ScnSrc,setScnSrc] = useState(ScnSrc1);
  ChrSrc = data.chrImg == null ? ChrSrc : 'chars/' + data.chrImg;

  const [animate, setAnimator] = useState(0);

  useEffect(() => {
    setAnimator(1);
    setTimeout(() => {
      setScnSrc(ScnSrcOg);
    }, 500);
  }, [ScnSrcOg]);

  const [typeEffect] = useTypewriter({
    words: [data.body == null ? '' : data.body],
    loop: {},
    typeSpeed: 45,
    deleteSpeed: 33,
  });

  return (
    <div className="DialogueBox">
      <img
        className="ScnImg"
        src={ScnSrc}
        title="Scene - X"
        alt={'Scene - X' + ' - Image Not Found'}
        onAnimationEnd={() => setAnimator(0)}
        animate={animate}
      />
      <img
        className="ChrImg"
        src={ChrSrc}
        title={data.name}
        alt={data.name + ' - Image Not Found'}
        onAnimationEnd={() => setAnimator(0)}
        animate={animate}
      />
      <div className="DialogueBG">
        <div className="NameTag">{data.name}</div>
        <div className="Dialogues">{typeEffect}</div>
        <div className="Next">
          <button
            className="NextButton"
            onClick={() => {
              setCounter(Next);
            }}
          >
            Next - {counter}
          </button>
        </div>
      </div>
    </div>
  );
}

export default SceneHandler;
