import './SceneHandler.css';
import Typewriter from './ShowDialogue';
import { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faHome,
  faCirclePlay,
  faVolumeUp,
  faInfoCircle,
  faLaughWink,
  faNeuter,
  faSadTear,
  faAngry,
  faSurprise,
  faFaceFrown,
  faFaceDizzy,
  faFaceFlushed,
  faLaughBeam,
  faFaceLaughBeam,
  faAdd,
  faFaceGrimace,
  faFaceSurprise,
} from '@fortawesome/free-solid-svg-icons';

var Next = 0;
var Alt = 0;

var ScnSrcOg = 'scenes/sampleScene.png';
var ScnSrc = 'scenes/sampleScene.png';
var ChrSrcOg = 'chars/sampleBob.png';
var ChrSrc = 'chars/sampleBob.png';

function SceneHandler() {
  const [counter, setCounter] = useState(0);
  const [data, setData] = useState([{}]);

  const handleTypewriterFinish = () => {
    setShowNext(true);
  };

  useEffect(() => {
    fetch('http://localhost:5001/dialogue/' + counter)
      .then((resp) => resp.json())
      .catch((error) => {
        const K = {
          name: 'Error',
          body: 'Error',
          scnImg: 'Error',
          chrImg: 'Error',
        };
        setData(JSON.parse(K));
      })
      .then((dialogue) => {
        setData(dialogue);
      });
  }, [counter]);

  Next = data.Next;
  Alt = data.Skip;

  ScnSrcOg = data.scnImg == null ? ScnSrcOg : 'scenes/' + data.scnImg;
  const [ScnSrc, setScnSrc] = useState(ScnSrcOg);

  ChrSrcOg = data.chrImg == null ? ChrSrcOg : 'chars/' + data.chrImg;
  const [ChrSrc, setChrSrc] = useState(ChrSrcOg);

  const [animate_scene, setAnimatorScn] = useState(0);
  const [animate_chars, setAnimatorChr] = useState(0);

  useEffect(() => {
    setAnimatorScn(1);
    setTimeout(() => {
      setScnSrc(ScnSrcOg);
    }, 500);
  }, [ScnSrcOg.toString()]);

  useEffect(() => {
    setAnimatorChr(1);
    setTimeout(() => {
      setChrSrc(ChrSrcOg);
    }, 500);
  }, [ChrSrcOg.toString()]);

  const [showNext, setShowNext] = useState(false);
  const [on_Start, setStart] = useState(true);

  return (
    <div className="DialogueBox">
      <img
        className="ScnImg"
        src={ScnSrc}
        title="Scene - X"
        alt={'Scene - X' + ' - Image Not Found'}
        onAnimationEnd={() => setAnimatorScn(0)}
        animate_scene={animate_scene}
      />
      <img
        className="ChrImg"
        src={ChrSrc}
        title={data.name}
        alt={data.name + ' - Image Not Found'}
        onAnimationEnd={() => setAnimatorChr(0)}
        animate_chars={animate_chars}
      />
      <div className="DialogueBG">
        <div className="NameTag">{data.name}</div>
        <div className="Dialogues">
          <Typewriter
            settingStart={setStart}
            onStart={on_Start}
            text={data.body == null ? '' : data.body}
            speed={50}
            onFinish={handleTypewriterFinish}
          />
        </div>
        <div className="Next">
          <ul className="Emojis">
            <li>
              <FontAwesomeIcon title="Happy" icon={faFaceLaughBeam} />
            </li>
            <li>
              <FontAwesomeIcon title="Neutral" icon={faNeuter} />
            </li>
            <li>
              <FontAwesomeIcon title="Sad" icon={faFaceFrown} />
            </li>
            <li>
              <FontAwesomeIcon title="Angry" icon={faAngry} />
            </li>
            <li>
              <FontAwesomeIcon title="Surprise" icon={faFaceSurprise} />
            </li>
            <li>
              <FontAwesomeIcon title="Fear" icon={faFaceGrimace} />
            </li>
            <li>
              <FontAwesomeIcon title="Disgust" icon={faFaceDizzy} />
            </li>
          </ul>
          <button
            className="NextButton"
            onClick={() => {
              setCounter(Next);
              setStart(true);
            }}
          >
            {' '}
            Next - {counter}{' '}
          </button>
        </div>
      </div>
    </div>
  );
}

export default SceneHandler;
