import './SceneHandler.css';
import { useTypewriter, Cursor } from 'react-simple-typewriter';
import { useState, useEffect } from 'react';

var Next = 0;
var Alt = 0;

var ScnSrc = 'scenes/sampleScene.png';
var ChrSrc = 'chars/sampleBob.png';

// function inc_Counter()
// {
//     counter++;
//     console.log(counter);
// }

function SceneHandler() {
    // const a = StartGame();
    const [counter,setCounter] = useState(0);
    const [data,setData] = useState([{}])
    useEffect(()=> {
        console.log(counter)
        fetch('http://localhost:5001/dialogue/' + counter)
        .then(resp => resp.json())
        .catch(error =>
        {
            const K = {name : "Error", body : "Error", scnImg: "Error", chrImg: "Error"};
            setData(JSON.parse(K));
        })
        .then(dialogue =>
        {
            setData(dialogue);
            console.log(data);
        })
    console.log(data);
    // const name = data.name;
    // const dialogue = data.body;
    },[counter])
    Next = data.Next;
    Alt = data.Skip;

    ScnSrc = data.scnImg == null ? ScnSrc : "scenes/" + data.scnImg;
    ChrSrc = data.chrImg == null ? ChrSrc : "chars/" + data.chrImg;

    const [animate, setAnimator] = useState(false);

    const [typeEffect] = useTypewriter({
        words: [data.body == null ? "" : data.body],
        loop: {},
        typeSpeed: 45,
        deleteSpeed: 33
    });

    return (
      <div className="DialogueBox">
        <img
          className="ScnImg"
          src={ScnSrc}
          title="Scene - X"
          alt={'Scene - X' + ' - Image Not Found'}
          onAnimationEnd={() => setAnimator(0)}
          animate={animate}
        />
        <img
          className="ChrImg"
          src={ChrSrc}
          title={data.name}
          alt={data.name + ' - Image Not Found'}
          onAnimationEnd={() => setAnimator(0)}
          animate={animate}
        />
        <div className="DialogueBG">
          <div className="NameTag">{data.name}</div>
          <div className="Dialogues">{typeEffect}</div>
          <div className="Next">
            <button
              className="NextButton"
              onClick={() => {
                setCounter(Next);
                setAnimator(true);
                console.log("Hi")
              }}
            >
              Next - {counter}
            </button>
          </div>
        </div>
      </div>
    );
}

export default SceneHandler;
